    <div class="my-auto">
      <form autocomplete='off' class='form'>
        <div class='control'>
          <h1 class="h1 fs-1">
            Log In
          </h1>
          <h1>
            {{$store.state.auth.login_error}}
          </h1>
        </div>
        <div class='control block-cube block-input'>
          <input type="email" name="email" id="email" placeholder="Email" v-model="email">
          <div class='bg-top'>
            <div class='bg-inner'></div>
          </div>
          <div class='bg-right'>
            <div class='bg-inner'></div>
          </div>
          <div class='bg'>
            <div class='bg-inner'></div>
          </div>
        </div>
        <div class='control block-cube block-input'>
          <input type="password" name="password" placeholder="Password" id="password" v-model="password">
          <div class='bg-top'>
            <div class='bg-inner'></div>
          </div>
          <div class='bg-right'>
            <div class='bg-inner'></div>
          </div>
          <div class='bg'>
            <div class='bg-inner'></div>
          </div>
        </div>
        <button @click.prevent="login_data" class='btn block-cube block-cube-hover' type='submit'>
          <div class='bg-top'>
            <div class='bg-inner'></div>
          </div>
          <div class='bg-right'>
            <div class='bg-inner'></div>
          </div>
          <div class='bg'>
            <div class='bg-inner'></div>
          </div>
          <div class='text'>
            Log In
          </div>
        </button>
      </form>
    </div>
  </div>
</template>

<script>
import { mapActions } from 'vuex'
export default {
  name: 'Login',
  data () {
    return {
      email: '',
      password: ''
    }
  },
  created () {
    if (this.$store.state.auth.isAuthenticated) {
      this.$router.push('Dashboard')
    }
  },
  methods: {
    validateEmail (email) {
      var re = /\S+@\S+\.\S+/
      return re.test(email)
    },
    login_data () {
      if (this.email === '' || this.password === '') {
        alert('Please Enter Data')
        return
      }
      this.email = this.email.toLowerCase()
      if (!this.validateEmail(this.email)) {
        alert('Please Enter Email in correct Format')
        this.email = ''
        this.password = ''
        return
      }
      this.login_action({
        email: this.email,
        password: this.password
      })
      this.email = ''
      this.password = ''
    },
    ...mapActions({
      login_action: 'auth/login_action'
    })
  }
}
</script>

